import { useEffect, useState } from 'react';
import { Form, Card } from 'react-bootstrap';
import './FlightList.css';
import axios from 'axios';

export const FlightList = () => {

    const [flights, setFlights] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8085/flights')
            .then(res => setFlights(res.data));
    }, []);

    return (
        <div>
            {flights.map((flight, index) => {
                return (
                    <div key={flight._id} >
                        <Form>
                            <Form.Group float="left" className="mb-3" controlId="formFlightNumber">
                                <Form.Label>Flight Number: </Form.Label>
                                <Form.Text className="text-muted">{flight.flightNumber}</Form.Text>
                            </Form.Group>

                            <div style={{float: "left"}}>
                                <Form.Group className="mb-3" controlId="formDepartureInfo">
                                    <Form.Label>Departure Date & Time: </Form.Label>
                                    <Form.Text className="text-muted">{flight.departureDate.day}/{flight.departureDate.month}/{flight.departureDate.year}</Form.Text>
                                    <Form.Text className="text-muted"> {flight.departureTime.hour}:{flight.departureTime.minute} {(flight.departureTime.isBeforeMidday) ? 'A.M.' : 'P.M.'}</Form.Text>
                                </Form.Group>

                                <Form.Group className="mb-3" controlId="formArrivalInfo">
                                    <Form.Label>Arrival Date & Time: </Form.Label>
                                    <Form.Text className="text-muted">{flight.arrivalDate.day}/{flight.arrivalDate.month}/{flight.arrivalDate.year}</Form.Text>
                                    <Form.Text className="text-muted"> {flight.arrivalTime.hour}:{flight.arrivalTime.minute} {(flight.arrivalTime.isBeforeMidday) ? 'A.M.' : 'P.M.'}</Form.Text>
                                </Form.Group>
                            </div>

                            <Form.Group className="mb-3" controlId="formDepAirportInfo">
                                <Form.Label>Departure Airport: </Form.Label>
                                <Form.Text className="text-muted">{flight.departureAirport.airportName} ({flight.departureAirport.airportTag})</Form.Text>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formArrAirportInfo">
                                <Form.Label>Arrival Airport: </Form.Label>
                                <Form.Text className="text-muted">{flight.arrivalAirport.airportName} ({flight.arrivalAirport.airportTag})</Form.Text>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formPassengerInfo">
                                <Form.Label>Passenger Count: </Form.Label>
                                <Form.Text className="text-muted">{flight.passengerCount}</Form.Text>
                                <Form.Label> Passenger Limit: </Form.Label>
                                <Form.Text className="text-muted">{flight.passengerLimit}</Form.Text>
                            </Form.Group>
                        </Form>
                    </div>
                );
            })}
        </div>
    )
}
